import React, { useState } from 'react';
import axios from 'axios';

export default function AddStationForm() {
  const [formData, setFormData] = useState({
    name: '',
    latitude: '',
    longitude: '',
    sourceType: 'non-renewable',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:5000/api/locations', formData, {
        withCredentials: true,
      });

      if (res.status === 201) {
        alert('Charging station request submitted for admin approval!');
      }
    } catch (err) {
      console.error(err);
      alert('Error submitting request. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Charging Station</h2>
      <input name="name" placeholder="Station Name" value={formData.name} onChange={handleChange} required />
      <input name="latitude" placeholder="Latitude" type="number" value={formData.latitude} onChange={handleChange} required />
      <input name="longitude" placeholder="Longitude" type="number" value={formData.longitude} onChange={handleChange} required />
      <select name="sourceType" value={formData.sourceType} onChange={handleChange}>
        <option value="renewable">Renewable</option>
        <option value="non-renewable">Non-Renewable</option>
      </select>
      <button type="submit">Submit Request</button>
    </form>
  );
}
