import { useState } from 'react';
import AddChargerForm from './AddChargerForm';

export default function AdminChargingStations({ stations }) {
  const [showForm, setShowForm] = useState(false);
  const [selectedLocationId, setSelectedLocationId] = useState(null);

  const handleOpenForm = (locationId) => {
    setSelectedLocationId(locationId);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setSelectedLocationId(null);
    setShowForm(false);
  };

  return (
    <div>
      <h2>Charging Stations</h2>
      {stations.map((station) => (
        <div key={station._id} className="border p-4 mb-2 rounded-lg flex justify-between items-center">
          <span><strong>{station.name}</strong></span>
          <button
            className="bg-blue-600 text-white px-3 py-1 rounded"
            onClick={() => handleOpenForm(station._id)}
          >
            ➕ Add Charger
          </button>
        </div>
      ))}

      {/* Modal for AddChargerForm */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg w-full max-w-md">
            <h2 className="text-xl font-bold mb-4">Add Charger</h2>
            <AddChargerForm locationId={selectedLocationId} />
            <button
              className="mt-4 text-sm text-gray-600 hover:underline"
              onClick={handleCloseForm}
            >
              ✖ Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
