import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify'; // Make sure toast is set up in your project

const AddLocationForm = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    latitude: '',
    longitude: '',
    sourceType: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/locations/request', {
        name: formData.name,
        latitude: parseFloat(formData.latitude),
        longitude: parseFloat(formData.longitude),
        sourceType: formData.sourceType,
      }, {
        withCredentials: true,
      });

      if (res.status === 201) {
        toast.success('Charging station request submitted for admin approval!');
        if (onClose) onClose(); // Close the modal/form if passed as prop
      }
    } catch (err) {
      console.error(err);
      toast.error('Error submitting request. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ margin: '10px' }}>
      <input
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleChange}
        required
      />
      <input
        name="latitude"
        placeholder="Latitude"
        value={formData.latitude}
        onChange={handleChange}
        required
      />
      <input
        name="longitude"
        placeholder="Longitude"
        value={formData.longitude}
        onChange={handleChange}
        required
      />
      <input
        name="sourceType"
        placeholder="renewable or non-renewable"
        value={formData.sourceType}
        onChange={handleChange}
        required
        pattern="renewable|non-renewable"
      />
      <button type="submit">Add Location</button>
    </form>
  );
};

export default AddLocationForm;
